import createAiProposal from "./create-ai-proposal"

const craftUniqueProposal = async  (items: any) => {

  const itemsWithProposal = await Promise.all(items.map(async (item: any) => {
    const uniqueProposal = await createAiProposal(item.content)

    return {
      ...item,
      uniqueProposal
    }
  }))
  return itemsWithProposal
}


export default craftUniqueProposal;